import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { EventModalComponent } from '../event-modal/event-modal.component';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { environment } from '../../environments/environment';
import { EventService } from '../event.service';

const apiEndpoint = environment.apiEndpoint;

@Component({
    selector: 'app-event-list',
    templateUrl: './event-list.page.html',
    styleUrls: ['./event-list.page.scss'],
  })

export class EventListPage implements OnInit {
  constructor(private http: HttpClient,private modalController: ModalController, private eventService : EventService) { }

  ngOnInit() {
    this.loadEvents();
  }

  selectedCardIndex: number = -1;

  events: any[] = [];

  async presentModal(event:any,index: number) {
    this.selectedCardIndex = index;

    const modal = await this.modalController.create({
      component: EventModalComponent,
      backdropDismiss: true,
      componentProps: {
        event: event,
      },
    });

    await modal.present();
  }

  loadEvents() {
    const apiUrl = apiEndpoint; // Replace with your API endpoint
    this.http.get<any[]>(apiUrl)
      .pipe(
        catchError((error) => {
          console.error(error);
          return throwError('Error occurred while fetching events.'); // Customize the error message as needed
        })
      )
      .subscribe((response) => {
        this.events = response;
      });
    }

}
