import { Component, OnInit } from '@angular/core';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-booking-page',
  templateUrl: './booking-page.page.html',
  styleUrls: ['./booking-page.page.scss'],
})
export class BookingPagePage implements OnInit {

  constructor(private cartService: CartService) { }

  cartItems: any[] = [];
  cartCrews: any[] = [];
  eventsTotalPrice: number = 0;
  crewsTotalPrice: number = 0;
  totalPrice: number = 0;

  ngOnInit() {
    this.cartItems = this.cartService.getCartItems();
    this.cartCrews = this.cartService.getSelectedCrews();
    console.log(this.crewsTotalPrice);
  }

  ionViewWillEnter() {
    this.eventsTotalPrice = this.cartService.getEventTotalPrice();
    this.crewsTotalPrice = this.cartService.getCrewsTotalPrice();
    console.log(this.eventsTotalPrice)
    console.log(this.crewsTotalPrice);
    this.totalPrice = this.eventsTotalPrice + this.crewsTotalPrice;
    console.log(this.totalPrice);
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Booking</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <!-- Use ion-card to displayevent-->
    <ion-card *ngFor="let item of cartItems">
      <!-- Item image (if available) -->
      <ion-img [src]="item.image"></ion-img>
      <ion-card-header>
        <ion-card-title>{{ item.title }}</ion-card-title>
      </ion-card-header>
      <ion-card-content>
        <!-- Item price -->
        <p>Price: {{ item.price }}</p>
      </ion-card-content>
    </ion-card>
  </ion-list>

  <ion-list>
    <!-- Use ion-card to display each item in the cart for crews -->
    <ion-card *ngFor="let crew of cartCrews">
      <!-- Item image (if available) -->
      <ion-img [src]="crew.image"></ion-img>
      <ion-card-header>
        <ion-card-title>{{ crew.title }}</ion-card-title>
      </ion-card-header>
      <ion-card-content>
        <!-- Item price -->
        <p>Price: {{ crew.price }}</p>
      </ion-card-content>
    </ion-card>
  </ion-list>

  <!-- Display the total price of events -->
  <ion-label>
    Events Total Price: {{ eventsTotalPrice }}
  </ion-label>

  <!-- Display the total price of crews -->
  <ion-label>
    Crews Total Price: {{ crewsTotalPrice }}
  </ion-label>

  <!-- Display the overall total price -->
  <ion-label>
    Overall Total Price: {{ totalPrice }}
  </ion-label>
</ion-content>
