import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-crew-modal',
  templateUrl: './crew-modal.component.html',
  styleUrls: ['./crew-modal.component.scss'],
})
export class CrewModalComponent  implements OnInit {

  constructor(private modalController: ModalController) { }

  ngOnInit() {}

  crew: any;
  selectedCrews: any[] = [];

  addCrewToCart(crew: any): void {
    // Use CartService to add the crew to the selectedCrews array
    this.cartService.addCrewToCart(crew);
  }

  removeCrewFromCart(crew: any): void {
    // Use CartService to remove the crew from the selectedCrews array
    this.cartService.removeCrewFromCart(crew);
  }

  dismiss() {
    this.modalController.dismiss();
  }

  onCloseModal() {
    this.modalController.dismiss({
      dismissed: true,
    });
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title class="ion-text-center">
      {{crew.title}}
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>{{crew.title}}</ion-card-title>
    </ion-card-header>
    <img alt="Crew Image" [src]="crew.image" />
    <ion-card-content>
      <ion-text>
        {{crew.extendedDescription}}
      </ion-text>
      <ion-grid>
        <ion-row>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{crew.location}}</span>
              <span class="subtitle">Location</span>
            </div>
          </ion-col>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{crew.pax}}</span>
              <span class="subtitle">Pax</span>
            </div>
          </ion-col>
        </ion-row>
      </ion-grid>
      <ion-grid>
        <ion-row>
          <ion-col class="price-text">RM{{crew.price}}</ion-col>
        </ion-row>
      </ion-grid>
      <ion-button expand="full" (click)="addCrewToCart(crew)">Add to Cart</ion-button>
      <ion-button expand="full" (click)="dismiss()">Close</ion-button>
    </ion-card-content>
  </ion-card>
</ion-content>
