import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { EventModalComponent } from '../event-modal/event-modal.component';

@Component({
  selector: 'app-event-list',
  templateUrl: './event-list.page.html',
  styleUrls: ['./event-list.page.scss'],
})
export class EventListPage implements OnInit {

  constructor(private modalController: ModalController) { }

  ngOnInit() {
  }

  selectedCardIndex: number = -1;

  cards: any[] = [
    {
      title: 'General Event',
      description: 'This is perfect for general events.',
      image: 'https://shoalhavenentertainment.com.au/Sites/SEC/Images/Tiles/sec-image_plan-event_functions-events.jpg?ver=2019-10-18-101601-240'
    },
    {
      title: 'Birthday Event',
      description: 'Celebrate your birthday with us.',
      image: 'https://todaysparent.mblycdn.com/tp/resized/2017/09/767x431/art_themed_birthday_party.jpg'
    },
    {
      title: 'Wedding Event',
      description: 'We will make your wedding memorable.',
      image: 'https://www.hananagroup.com/wp-content/uploads/2020/01/EPY09640-min-scaled.jpg'
    },
  ];

  async presentModal(index: number) {
    this.selectedCardIndex = index;

    const modal = await this.modalController.create({
      component: EventModalComponent, // Replace with your modal component
      componentProps: {
        // Add any input properties you want to pass to the modal component
      },
    });

    await modal.present();
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>
      Event
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card
    *ngFor="let card of cards; let i = index"
    [class.selected-card]="selectedCardIndex === i"
    (click)="presentModal(i)"
  >
    <img alt="Card Image" [src]="card.image" />
    <ion-card-header>
      <ion-card-title>
        {{ card.title }}
      </ion-card-title>
    </ion-card-header>

    <ion-card-content>
      {{ card.description }}
    </ion-card-content>
  </ion-card>
</ion-content>
