import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { CrewModalComponent } from '../crew-modal/crew-modal.component';

@Component({
  selector: 'app-crew-list',
  templateUrl: './crew-list.page.html',
  styleUrls: ['./crew-list.page.scss'],
})
export class CrewListPage implements OnInit {

  constructor(private modalController: ModalController) { }

  ngOnInit() {
  }

  crews: any[] = [];

  loadCrews() {
    const apiUrl = apiEndpoint; // Replace with your API endpoint
    this.http.get<any[]>(apiUrl)
      .pipe(
        catchError((error) => {
          console.error(error);
          return throwError('Error occurred while fetching events.'); // Customize the error message as needed
        })
      )
      .subscribe((response) => {
        this.events = response;
      });
    }


  selectedCardIndex: number = -1;

  async presentModal(crew:any,index: number) {
    this.selectedCardIndex = index;

    const modal = await this.modalController.create({
      component: CrewModalComponent,
      backdropDismiss: true,
      componentProps: {
        crew: crew,
      },
    });

    await modal.present();
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>
      Crew
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card
    *ngFor="let crew of crews;let i = index"
    [class.selected-card]="selectedCardIndex === i"
    (click)="presentModal(crew,i)"
  >
    <img alt="Crew Image" src="{{ crew.image }}" />
    <ion-card-header>
      <ion-card-title>
        {{ crew.title }}
      </ion-card-title>
    </ion-card-header>

    <ion-card-content>
      {{ crew.description }}
    </ion-card-content>
  </ion-card>
</ion-content>
