import { Component, OnInit } from '@angular/core';
import { ModalController, AlertController } from '@ionic/angular';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-event-modal',
  templateUrl: './event-modal.component.html',
  styleUrls: ['./event-modal.component.scss'],
})
export class EventModalComponent  implements OnInit {

  constructor(private modalController: ModalController, private cartService: CartService) { }

  ngOnInit() {
    this.addAttributes();
    this.isEventInCart = this.cartService.isEventInCart(this.event);
  }

  event: any;
  isEventInCart: boolean = false;

  addAttributes() {
    this.event.booked = false;
    this.event.type = 'event';
  }

  dismiss() {
    this.modalController.dismiss();
  }

  onCloseModal() {
    this.modalController.dismiss({
      dismissed: true,
    });
  }

  onBook(event: any) {
    event.booked = true
    console.log(event);
    console.log('Booked!');
    this.cartService.addToCart(event); // Add the booked event to the cart
  }

  check(event: any){
    console.log(event);
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title class="ion-text-center">
      {{event.title}}
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>{{event.title}}</ion-card-title>
    </ion-card-header>
    <img alt="Card Image" [src]="event.image" />
    <ion-card-content>
      <ion-text>
        {{event.extendedDescription}}
      </ion-text>
      <ion-grid>
        <ion-row>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{event.location}}</span>
              <span class="subtitle">Location</span>
            </div>
          </ion-col>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{event.pax}}</span>
              <span class="subtitle">Pax</span>
            </div>
          </ion-col>
        </ion-row>
      </ion-grid>
      <ion-grid>
        <ion-row>
          <ion-col class="price-text">RM{{event.price}}</ion-col>
        </ion-row>
      </ion-grid>
      <ion-button expand="full" (click)="check(event)">Check</ion-button>
      <ion-button expand="full" (click)="onBook(event)" [disabled]="event.booked">Book</ion-button>
      <ion-button expand="full" (click)="dismiss()">Close</ion-button>
      <ion-content>
      </ion-content>
    </ion-card-content>
  </ion-card>
</ion-content>
