import { Component, OnInit } from '@angular/core';
import { ModalController, NavParams } from '@ionic/angular';

@Component({
  selector: 'app-event-modal',
  templateUrl: './event-modal.component.html',
  styleUrls: ['./event-modal.component.scss'],
})
export class EventModalComponent  implements OnInit {

  constructor(private modalController: ModalController, private navParams: NavParams) {}

  ngOnInit() {
    const event = this.navParams.get('event');
    if (event && event.id) {
      console.log('Selected Event ID:', event.id);
    }
  }

  event: any;
  selectedEvent: any;

  dismiss() {
    this.modalController.dismiss();
  }

  onCloseModal() {
    this.modalController.dismiss({
      dismissed: true,
    });
  }

  onAddEvent() {
    const event = this.navParams.get('event');
    this.selectedEvent = event.id;
    console.log('Selected Event:', this.selectedEvent);
    this.modalController.dismiss({
      selectedEvent: this.navParams.get('event'),
    });
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title class="ion-text-center">
      {{event.title}}
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>{{event.title}}</ion-card-title>
    </ion-card-header>
    <img alt="Card Image" [src]="event.image" />
    <ion-card-content>
      <ion-text>
        {{event.extendedDescription}}
      </ion-text>
      <ion-grid>
        <ion-row>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{event.location}}</span>
              <span class="subtitle">Location</span>
            </div>
          </ion-col>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{event.pax}}</span>
              <span class="subtitle">Pax</span>
            </div>
          </ion-col>
        </ion-row>
      </ion-grid>
      <ion-grid>
        <ion-row>
          <ion-col class="price-text">RM{{event.price}}</ion-col>
        </ion-row>
      </ion-grid>
      <ion-button expand="full" (click)="onAddEvent()">Add</ion-button>
      <ion-button expand="full" (click)="dismiss()">Close</ion-button>
    </ion-card-content>
  </ion-card>
  <ion-backdrop (ionBackdropTap)="onCloseModal()"></ion-backdrop>
</ion-content>
