import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';

@Component({
  selector: 'app-event-modal',
  templateUrl: './event-modal.component.html',
  styleUrls: ['./event-modal.component.scss'],
})
export class EventModalComponent  implements OnInit {

  constructor(private modalController: ModalController) { }

  ngOnInit() {}

  card: any;

  dismiss() {
    this.modalController.dismiss();
  }

  onCloseModal() {
    this.modalController.dismiss({
      dismissed: true,
    });
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title class="ion-text-center">
      {{card.title}}
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>{{card.title}}</ion-card-title>
    </ion-card-header>
    <img alt="Card Image" [src]="card.image" />
    <ion-card-content>
      <ion-text>
        {{card.extendedDescription}}
      </ion-text>
      <ion-grid>
        <ion-row>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{card.location}}</span>
              <span class="subtitle">Location</span>
            </div>  
          </ion-col>
          <ion-col>
            <div class="two-row-text">
              <span class="text-title">{{card.pax}}</span>
              <span class="subtitle">Pax</span>
            </div>  
          </ion-col>
        </ion-row>
      </ion-grid>
      <ion-text>
        {{card.price}}
      </ion-text>
      <ion-button expand="full" (click)="dismiss()">Close</ion-button>
    </ion-card-content>
  </ion-card>
  <ion-backdrop (ionBackdropTap)="onCloseModal()"></ion-backdrop>
</ion-content>
