import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-event-list',
  templateUrl: './event-list.page.html',
  styleUrls: ['./event-list.page.scss'],
})
export class EventListPage implements OnInit {

  constructor() { }

  ngOnInit() {
  }

  selectedCardIndex: number = -1;

  cards: any[] = [
    {
      title: 'General Event',
      description: 'This is perfect for general events.',
      image: 'https://shoalhavenentertainment.com.au/Sites/SEC/Images/Tiles/sec-image_plan-event_functions-events.jpg?ver=2019-10-18-101601-240'
    },
    {
      title: 'Birthday Event',
      description: 'Celebrate your birthday with us.',
      image: 'https://todaysparent.mblycdn.com/tp/resized/2017/09/767x431/art_themed_birthday_party.jpg'
    },
    {
      title: 'Wedding Event',
      description: 'We will make your wedding memorable.',
      image: 'https://www.hananagroup.com/wp-content/uploads/2020/01/EPY09640-min-scaled.jpg'
    },
  ];

  cardClicked(card: any) {
    // Handle the card click event
    console.log('Card clicked:', card);
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>
      Event
    </ion-title>
  </ion-toolbar>
</ion-header>


<ion-content>
  <ion-card *ngFor="let card of cards" (click)="cardClicked(card)">
    <img alt="Card Image" [src]="card.image" />
    <ion-card-header>
      <ion-card-title>
        {{ card.title }}
      </ion-card-title>
    </ion-card-header>

    <ion-card-content>
      {{ card.description }}
    </ion-card-content>
  </ion-card>
</ion-content>
